class Question:
    def __init__(self, text, choices, answer, explanation):
        self.text = text
        self.choices = choices
        self.answer = answer
        self.explanation = explanation


class QuizSet:
    def __init__(self):
        self.quizzes = {}
        self.current_quiz_used_questions = set()

    def add_quiz(self, quiz_name, questions):
        self.quizzes[quiz_name] = questions

    def get_random_unused_question(self, quiz_name):
        questions = self.quizzes[quiz_name]
        available_questions = [q for q in questions if q not in self.current_quiz_used_questions]
        if not available_questions:
            return None

        def question_generator():
            while True:
                if not available_questions:
                    break
                question = random.choice(available_questions)
                self.current_quiz_used_questions.add(question)
                yield question

        return question_generator()

    def present_question(self, question, question_number):
        print(f"题号：{question_number}")
        print(question.text)
        letter = 'A'
        for choice in question.choices:
            print(f"{letter}. {choice}")
            letter = chr(ord(letter) + 1)

    def check_answer(self, user_answer, question):
        return user_answer.upper() == question.answer.upper()

    def show_explanation(self, question):
        print(f"解析：{question.explanation}")

    def run_quiz(self, quiz_name):
        self.current_quiz_used_questions = set()
        score = 0
        questions = self.quizzes[quiz_name]
        if len(questions) < 1000:
            print(f"此试卷题目数量为{len(questions)}。")
        num_questions = min(len(questions), 1000)
        question_number = 1

        time_limit = input("请输入每道题的做题时间限制（秒）：")
        try:
            time_limit = int(time_limit)
        except ValueError:
            print("输入无效，采用默认时间限制10秒。")
            time_limit = 10

        while True:
            question_generator = self.get_random_unused_question(quiz_name)
            if question_generator is None:
                print(f"所有题目已完成，{quiz_name}测试结束。")
                break
            question = next(question_generator)
            self.present_question(question, question_number)
            start_time = time.time()
            user_choice = None
            user_answered = False
            while True:
                if time.time() - start_time > time_limit:
                    print("时间到，你超时未作答，本题计0分，自动跳出题库程序。")
                    question_number += 1
                    user_answered = False
                    break
                user_choice = input("请输入你的答案选项字母（输入'R'重新显示问题，输入'Q'退出测试）：")
                if user_choice.upper() in [chr(ord('A') + i) for i in range(len(question.choices))]:
                    user_answered = True
                    break
                elif user_choice.lower() == 'r':
                    self.present_question(question, question_number)
                elif user_choice.lower() == 'q':
                    print(f"你选择退出测试。{quiz_name}测试提前结束。")
                    return
                else:
                    if isinstance(user_choice, str) and len(user_choice) == 1:
                        print("输入的选项字母不在有效范围内，请重新输入。")
                    else:
                        print("输入的内容不是有效的选项字母，请重新输入。")
            if user_choice is not None:
                index = ord(user_choice.upper()) - ord('A')
                user_answer = question.choices[index]
                if self.check_answer(user_answer, question):
                    print("答案正确！")
                    score += 3
                else:
                    print(f"错误。正确答案是 {question.answer}。")
                self.show_explanation(question)
                question_number += 1

        print(f"你的总分为：{score}/{num_questions * 3}。")
        send_back_choice = input("是否返回试卷选择界面？(Y/N)：")
        if send_back_choice.upper() == 'Y' or send_back_choice.upper() == 'y':
            return True
        else:
            print("试卷未返回选择界面。")

        repeat_choice = input("是否再次选择试卷进行测试？(Y/N)：")
        if repeat_choice.upper() == 'Y':
            return True
        else:
            return False


def generate_random_string(length=6):
    letters_and_digits = string.ascii_letters + string.digits
    return ''.join(random.choice(letters_and_digits) for _ in range(length))


