def add_new_quiz():
    while True:
        quiz_name = input("请输入新试卷的名称：")
        questions = []
        count = 0
        while count < 100:
            question_text = input("请输入问题文本（输入'Q'退出问题输入）：")
            if question_text.upper() == 'Q':
                if count < 100:
                    print("还需要输入更多问题以达到 100 题的要求。")
                else:
                    break
            choices = []
            while True:
                choice = input("请输入选项（输入'Q'退出选项输入）：")
                if choice.upper() == 'Q':
                    break
                choices.append(choice)
            answer = input("请输入正确答案：")
            explanation = input("请输入问题解释：")
            questions.append(Question(question_text, choices, answer, explanation))
            count += 1
        quiz_set = QuizSet()
        quiz_set.add_quiz(quiz_name, questions)
        print(f"成功添加试卷：{quiz_name}，题目数量为{len(questions)}。")
        another = input("是否继续添加试卷？（Y/N）")
        if another.upper()!= 'Y' and another.upper()!= 'y':
            break


